/**
 * VoiceAssist Design Tokens - Breakpoints
 * Responsive design system with mobile-first approach
 *
 * Features:
 * - Mobile-first breakpoint scale
 * - Container width constraints
 * - Semantic breakpoint names
 * - Media query helpers
 *
 * @example
 * ```typescript
 * import { breakpoints, containers, mediaQueries } from '@voiceassist/design-tokens';
 *
 * // Use in CSS-in-JS
 * const styles = {
 *   [mediaQueries.md]: {
 *     padding: '1.5rem',
 *   },
 * };
 *
 * // Use in Tailwind config
 * module.exports = {
 *   theme: {
 *     screens: breakpoints,
 *   },
 * };
 * ```
 */
/**
 * Breakpoint Values
 * Mobile-first approach: styles apply at this width and above
 *
 * Based on common device sizes:
 * - xs: Small phones (portrait)
 * - sm: Large phones, small tablets (portrait)
 * - md: Tablets (portrait), small laptops
 * - lg: Laptops, tablets (landscape)
 * - xl: Desktops
 * - 2xl: Large desktops, monitors
 */
declare const breakpoints: {
    /** Extra small - 480px (small phones) */
    readonly xs: "480px";
    /** Small - 640px (large phones, small tablets) */
    readonly sm: "640px";
    /** Medium - 768px (tablets portrait) */
    readonly md: "768px";
    /** Large - 1024px (tablets landscape, laptops) */
    readonly lg: "1024px";
    /** Extra large - 1280px (desktops) */
    readonly xl: "1280px";
    /** 2x Extra large - 1536px (large desktops) */
    readonly "2xl": "1536px";
};
/**
 * Breakpoint Values as Numbers (in pixels)
 * Useful for JavaScript calculations
 */
declare const breakpointValues: {
    readonly xs: 480;
    readonly sm: 640;
    readonly md: 768;
    readonly lg: 1024;
    readonly xl: 1280;
    readonly "2xl": 1536;
};
/**
 * Container Max Widths
 * Maximum content widths at each breakpoint
 * Provides comfortable reading and layout constraints
 */
declare const containers: {
    /** Extra small - full width with padding */
    readonly xs: "100%";
    /** Small - 600px */
    readonly sm: "600px";
    /** Medium - 720px */
    readonly md: "720px";
    /** Large - 960px */
    readonly lg: "960px";
    /** Extra large - 1140px */
    readonly xl: "1140px";
    /** 2x Extra large - 1320px */
    readonly "2xl": "1320px";
    /** Full - 100% (no constraint) */
    readonly full: "100%";
    /** Prose - optimal reading width (65-75 characters) */
    readonly prose: "65ch";
    /** Narrow - for forms, cards (500px) */
    readonly narrow: "500px";
    /** Wide - for dashboards (1600px) */
    readonly wide: "1600px";
};
/**
 * Media Query Strings
 * Ready-to-use media query strings for CSS-in-JS
 */
declare const mediaQueries: {
    /** Min-width: 480px */
    readonly xs: "@media (min-width: 480px)";
    /** Min-width: 640px */
    readonly sm: "@media (min-width: 640px)";
    /** Min-width: 768px */
    readonly md: "@media (min-width: 768px)";
    /** Min-width: 1024px */
    readonly lg: "@media (min-width: 1024px)";
    /** Min-width: 1280px */
    readonly xl: "@media (min-width: 1280px)";
    /** Min-width: 1536px */
    readonly "2xl": "@media (min-width: 1536px)";
    /** Max-width: 479px */
    readonly xsMax: "@media (max-width: 479px)";
    /** Max-width: 639px */
    readonly smMax: "@media (max-width: 639px)";
    /** Max-width: 767px */
    readonly mdMax: "@media (max-width: 767px)";
    /** Max-width: 1023px */
    readonly lgMax: "@media (max-width: 1023px)";
    /** Max-width: 1279px */
    readonly xlMax: "@media (max-width: 1279px)";
    /** Max-width: 1535px */
    readonly "2xlMax": "@media (max-width: 1535px)";
    /** 480px to 639px */
    readonly xsOnly: "@media (min-width: 480px) and (max-width: 639px)";
    /** 640px to 767px */
    readonly smOnly: "@media (min-width: 640px) and (max-width: 767px)";
    /** 768px to 1023px */
    readonly mdOnly: "@media (min-width: 768px) and (max-width: 1023px)";
    /** 1024px to 1279px */
    readonly lgOnly: "@media (min-width: 1024px) and (max-width: 1279px)";
    /** 1280px to 1535px */
    readonly xlOnly: "@media (min-width: 1280px) and (max-width: 1535px)";
    /** User prefers reduced motion */
    readonly reducedMotion: "@media (prefers-reduced-motion: reduce)";
    /** User prefers more motion (default) */
    readonly motionOK: "@media (prefers-reduced-motion: no-preference)";
    /** User prefers high contrast */
    readonly highContrast: "@media (prefers-contrast: high)";
    /** User prefers dark mode */
    readonly darkMode: "@media (prefers-color-scheme: dark)";
    /** User prefers light mode */
    readonly lightMode: "@media (prefers-color-scheme: light)";
    /** Touch-capable devices */
    readonly touch: "@media (hover: none) and (pointer: coarse)";
    /** Mouse/trackpad devices */
    readonly mouse: "@media (hover: hover) and (pointer: fine)";
    /** Devices with hover support */
    readonly hover: "@media (hover: hover)";
    /** Portrait orientation */
    readonly portrait: "@media (orientation: portrait)";
    /** Landscape orientation */
    readonly landscape: "@media (orientation: landscape)";
    /** Print styles */
    readonly print: "@media print";
    /** Screen only (not print) */
    readonly screen: "@media screen";
};
/**
 * Semantic Breakpoint Names
 * Human-readable names for common use cases
 */
declare const deviceBreakpoints: {
    /** Mobile phones (< 640px) */
    readonly mobile: "640px";
    /** Tablets (>= 768px) */
    readonly tablet: "768px";
    /** Laptops and desktops (>= 1024px) */
    readonly desktop: "1024px";
    /** Large monitors (>= 1280px) */
    readonly largeDesktop: "1280px";
};
/**
 * Grid Columns at Each Breakpoint
 * Default column counts for responsive grids
 */
declare const gridColumns: {
    /** Extra small - 1 column */
    readonly xs: 1;
    /** Small - 2 columns */
    readonly sm: 2;
    /** Medium - 3 columns */
    readonly md: 3;
    /** Large - 4 columns */
    readonly lg: 4;
    /** Extra large - 4 columns */
    readonly xl: 4;
    /** 2x Extra large - 6 columns */
    readonly "2xl": 6;
};
/**
 * Sidebar Widths at Each Breakpoint
 * Common sidebar widths for app layouts
 */
declare const sidebarWidths: {
    /** Collapsed - icon only (64px) */
    readonly collapsed: "64px";
    /** Compact - narrow (200px) */
    readonly compact: "200px";
    /** Default - standard (256px) */
    readonly default: "256px";
    /** Wide - spacious (320px) */
    readonly wide: "320px";
    /** Full - mobile overlay (100%) */
    readonly full: "100%";
};
/**
 * Header Heights at Each Breakpoint
 */
declare const headerHeights: {
    /** Mobile header (56px) */
    readonly mobile: "56px";
    /** Desktop header (64px) */
    readonly desktop: "64px";
    /** Compact header (48px) */
    readonly compact: "48px";
};
/**
 * Type Exports
 */
type Breakpoint = keyof typeof breakpoints;
type Container = keyof typeof containers;
type MediaQuery = keyof typeof mediaQueries;
type DeviceBreakpoint = keyof typeof deviceBreakpoints;
/**
 * Helper Functions
 */
/**
 * Check if window matches a breakpoint (client-side only)
 */
declare function matchesBreakpoint(breakpoint: Breakpoint): boolean;
/**
 * Get current breakpoint name (client-side only)
 */
declare function getCurrentBreakpoint(): Breakpoint | null;
/**
 * Create a min-width media query string
 */
declare function minWidth(px: number | string): string;
/**
 * Create a max-width media query string
 */
declare function maxWidth(px: number | string): string;
/**
 * Create a width range media query string
 */
declare function between(minPx: number | string, maxPx: number | string): string;
/**
 * Tailwind-compatible screens config
 */
declare const tailwindScreens: {
    readonly xs: "480px";
    readonly sm: "640px";
    readonly md: "768px";
    readonly lg: "1024px";
    readonly xl: "1280px";
    readonly "2xl": "1536px";
};

export { type Breakpoint, type Container, type DeviceBreakpoint, type MediaQuery, between, breakpointValues, breakpoints, containers, deviceBreakpoints, getCurrentBreakpoint, gridColumns, headerHeights, matchesBreakpoint, maxWidth, mediaQueries, minWidth, sidebarWidths, tailwindScreens };
