/**
 * VoiceAssist Design Tokens - Elevation
 * Semantic elevation system for visual hierarchy and depth
 *
 * Features:
 * - Material Design-inspired elevation levels
 * - Light and dark mode support
 * - Semantic naming for consistent usage
 * - Focus ring styles for accessibility
 *
 * @example
 * ```typescript
 * import { elevation, focusRings } from '@voiceassist/design-tokens';
 *
 * const cardStyles = {
 *   boxShadow: elevation.raised,
 * };
 *
 * const buttonStyles = {
 *   '&:focus-visible': {
 *     boxShadow: focusRings.default,
 *   },
 * };
 * ```
 */
/**
 * Raw Shadow Values
 * Building blocks for elevation system
 */
declare const shadowValues: {
    /** No shadow */
    readonly none: "none";
    /** Extra small - subtle depth hint */
    readonly xs: "0 1px 2px 0 rgba(0, 0, 0, 0.05)";
    /** Small - slight elevation */
    readonly sm: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
    /** Base/Medium - standard card shadow */
    readonly md: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1)";
    /** Large - elevated elements */
    readonly lg: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
    /** Extra large - prominent elements */
    readonly xl: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 8px 10px -6px rgba(0, 0, 0, 0.1)";
    /** 2x Extra large - highest elevation */
    readonly "2xl": "0 25px 50px -12px rgba(0, 0, 0, 0.25)";
    /** Inner shadow - inset elements */
    readonly inner: "inset 0 2px 4px 0 rgba(0, 0, 0, 0.05)";
};
/**
 * Dark Mode Shadow Values
 * Adjusted for dark backgrounds
 */
declare const shadowValuesDark: {
    readonly none: "none";
    readonly xs: "0 1px 2px 0 rgba(0, 0, 0, 0.2)";
    readonly sm: "0 1px 3px 0 rgba(0, 0, 0, 0.3), 0 1px 2px -1px rgba(0, 0, 0, 0.2)";
    readonly md: "0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -2px rgba(0, 0, 0, 0.2)";
    readonly lg: "0 10px 15px -3px rgba(0, 0, 0, 0.3), 0 4px 6px -4px rgba(0, 0, 0, 0.2)";
    readonly xl: "0 20px 25px -5px rgba(0, 0, 0, 0.3), 0 8px 10px -6px rgba(0, 0, 0, 0.2)";
    readonly "2xl": "0 25px 50px -12px rgba(0, 0, 0, 0.5)";
    readonly inner: "inset 0 2px 4px 0 rgba(0, 0, 0, 0.2)";
};
/**
 * Semantic Elevation Levels
 * Use these for consistent depth hierarchy
 *
 * Level 0: Base surface (no elevation)
 * Level 1: Raised elements (cards, panels)
 * Level 2: Overlay elements (dropdowns, tooltips)
 * Level 3: Modal backdrops
 * Level 4: Modal dialogs
 * Level 5: Floating elements (FABs)
 */
declare const elevation: {
    /** Level 0: Base surface - no shadow */
    readonly surface: "none";
    /** Level 1: Slightly raised - cards, tiles */
    readonly raised: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
    /** Level 2: Overlay - dropdowns, tooltips, popovers */
    readonly overlay: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1)";
    /** Level 3: Elevated - sticky headers, sidebars */
    readonly elevated: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
    /** Level 4: Modal - dialogs, sheets */
    readonly modal: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 8px 10px -6px rgba(0, 0, 0, 0.1)";
    /** Level 5: Floating - FABs, floating buttons */
    readonly floating: "0 25px 50px -12px rgba(0, 0, 0, 0.25)";
    /** Inset - input fields, pressed states */
    readonly inset: "inset 0 2px 4px 0 rgba(0, 0, 0, 0.05)";
};
/**
 * Dark Mode Elevation
 */
declare const elevationDark: {
    readonly surface: "none";
    readonly raised: "0 1px 3px 0 rgba(0, 0, 0, 0.3), 0 1px 2px -1px rgba(0, 0, 0, 0.2)";
    readonly overlay: "0 4px 6px -1px rgba(0, 0, 0, 0.3), 0 2px 4px -2px rgba(0, 0, 0, 0.2)";
    readonly elevated: "0 10px 15px -3px rgba(0, 0, 0, 0.3), 0 4px 6px -4px rgba(0, 0, 0, 0.2)";
    readonly modal: "0 20px 25px -5px rgba(0, 0, 0, 0.3), 0 8px 10px -6px rgba(0, 0, 0, 0.2)";
    readonly floating: "0 25px 50px -12px rgba(0, 0, 0, 0.5)";
    readonly inset: "inset 0 2px 4px 0 rgba(0, 0, 0, 0.2)";
};
/**
 * Focus Ring Styles
 * Accessible focus indicators for interactive elements
 *
 * WCAG 2.1 AA requires:
 * - Focus indicator must be visible
 * - Contrast ratio of at least 3:1
 * - At least 2px outline or equivalent
 */
declare const focusRings: {
    /** Default focus ring - primary color */
    readonly default: "0 0 0 3px rgba(59, 130, 246, 0.3)";
    /** Primary focus ring - stronger visibility */
    readonly primary: "0 0 0 3px rgba(59, 130, 246, 0.5)";
    /** Error focus ring - for error states */
    readonly error: "0 0 0 3px rgba(239, 68, 68, 0.3)";
    /** Success focus ring - for success states */
    readonly success: "0 0 0 3px rgba(34, 197, 94, 0.3)";
    /** Warning focus ring - for warning states */
    readonly warning: "0 0 0 3px rgba(245, 158, 11, 0.3)";
    /** Inset focus ring - for inputs */
    readonly inset: "inset 0 0 0 2px rgba(59, 130, 246, 0.5)";
    /** White focus ring - for dark backgrounds */
    readonly white: "0 0 0 3px rgba(255, 255, 255, 0.5)";
    /** None - explicitly no focus ring */
    readonly none: "none";
};
/**
 * Dark Mode Focus Rings
 * Adjusted opacity for dark backgrounds
 */
declare const focusRingsDark: {
    readonly default: "0 0 0 3px rgba(96, 165, 250, 0.4)";
    readonly primary: "0 0 0 3px rgba(96, 165, 250, 0.6)";
    readonly error: "0 0 0 3px rgba(248, 113, 113, 0.4)";
    readonly success: "0 0 0 3px rgba(74, 222, 128, 0.4)";
    readonly warning: "0 0 0 3px rgba(251, 191, 36, 0.4)";
    readonly inset: "inset 0 0 0 2px rgba(96, 165, 250, 0.6)";
    readonly white: "0 0 0 3px rgba(255, 255, 255, 0.3)";
    readonly none: "none";
};
/**
 * Component-Specific Shadows
 * Pre-defined shadows for common UI components
 */
declare const componentShadows: {
    /** Card - standard content container */
    readonly card: {
        readonly default: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
        readonly hover: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1)";
        readonly active: "0 1px 2px 0 rgba(0, 0, 0, 0.05)";
    };
    /** Button - interactive elements */
    readonly button: {
        readonly default: "0 1px 2px 0 rgba(0, 0, 0, 0.05)";
        readonly hover: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
        readonly active: "none";
        readonly disabled: "none";
    };
    /** Input - form fields */
    readonly input: {
        readonly default: "none";
        readonly focus: "inset 0 0 0 2px rgba(59, 130, 246, 0.5)";
        readonly error: "0 0 0 3px rgba(239, 68, 68, 0.3)";
    };
    /** Dropdown - menus and selects */
    readonly dropdown: {
        readonly default: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
        readonly nested: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1)";
    };
    /** Modal - dialogs and sheets */
    readonly modal: {
        readonly default: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 8px 10px -6px rgba(0, 0, 0, 0.1)";
        readonly backdrop: "0 0 0 100vmax rgba(0, 0, 0, 0.5)";
    };
    /** Toast - notifications */
    readonly toast: {
        readonly default: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
        readonly success: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1), inset 0 2px 0 0 rgba(34, 197, 94, 0.5)";
        readonly error: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1), inset 0 2px 0 0 rgba(239, 68, 68, 0.5)";
        readonly warning: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1), inset 0 2px 0 0 rgba(245, 158, 11, 0.5)";
        readonly info: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1), inset 0 2px 0 0 rgba(59, 130, 246, 0.5)";
    };
    /** Tooltip - contextual help */
    readonly tooltip: {
        readonly default: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1)";
    };
    /** Popover - interactive overlays */
    readonly popover: {
        readonly default: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
    };
    /** Sidebar - navigation panels */
    readonly sidebar: {
        readonly default: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
        readonly collapsed: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
    };
    /** Header - sticky navigation */
    readonly header: {
        readonly default: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
        readonly scrolled: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1)";
    };
    /** Fab - floating action button */
    readonly fab: {
        readonly default: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 8px 10px -6px rgba(0, 0, 0, 0.1)";
        readonly hover: "0 25px 50px -12px rgba(0, 0, 0, 0.25)";
        readonly active: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
    };
};
/**
 * Medical-Specific Component Shadows
 * Shadows for healthcare UI components
 */
declare const medicalComponentShadows: {
    /** Vital sign card - patient monitoring */
    readonly vitalSign: {
        readonly normal: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
        readonly warning: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1), 0 0 0 2px rgba(245, 158, 11, 0.3)";
        readonly critical: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1), 0 0 0 2px rgba(239, 68, 68, 0.5)";
    };
    /** Alert banner - clinical alerts */
    readonly alert: {
        readonly info: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1), inset 4px 0 0 0 rgba(59, 130, 246, 1)";
        readonly warning: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1), inset 4px 0 0 0 rgba(245, 158, 11, 1)";
        readonly critical: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1), inset 4px 0 0 0 rgba(239, 68, 68, 1)";
        readonly success: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1), inset 4px 0 0 0 rgba(34, 197, 94, 1)";
    };
    /** Medication card - drug information */
    readonly medication: {
        readonly default: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
        readonly interaction: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1), 0 0 0 2px rgba(245, 158, 11, 0.4)";
        readonly contraindicated: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1), 0 0 0 2px rgba(239, 68, 68, 0.4)";
    };
    /** Timeline - patient history */
    readonly timeline: {
        readonly default: "0 1px 2px 0 rgba(0, 0, 0, 0.05)";
        readonly active: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
    };
    /** Recording indicator - voice input */
    readonly recording: {
        readonly default: "0 0 0 4px rgba(239, 68, 68, 0.2)";
        readonly active: "0 0 0 8px rgba(239, 68, 68, 0.3)";
    };
};
/**
 * Type Exports
 */
type ShadowValue = keyof typeof shadowValues;
type ElevationLevel = keyof typeof elevation;
type FocusRing = keyof typeof focusRings;
type ComponentShadow = keyof typeof componentShadows;
type MedicalComponentShadow = keyof typeof medicalComponentShadows;
/**
 * Helper function to combine shadows
 */
declare function combineShadows(...shadows: string[]): string;
/**
 * Helper function to create color-tinted shadow
 */
declare function coloredShadow(color: string, opacity?: number, blur?: number, spread?: number): string;
/**
 * Helper function to get theme-appropriate shadow
 */
declare function getThemedShadow(level: ShadowValue, isDark?: boolean): string;
/**
 * Helper function to get theme-appropriate elevation
 */
declare function getThemedElevation(level: ElevationLevel, isDark?: boolean): string;
/**
 * Helper function to get theme-appropriate focus ring
 */
declare function getThemedFocusRing(type: FocusRing, isDark?: boolean): string;

export { type ComponentShadow, type ElevationLevel, type FocusRing, type MedicalComponentShadow, type ShadowValue, coloredShadow, combineShadows, componentShadows, elevation, elevationDark, focusRings, focusRingsDark, getThemedElevation, getThemedFocusRing, getThemedShadow, medicalComponentShadows, shadowValues, shadowValuesDark };
