};
declare const shadows: {
    readonly xs: "0 1px 2px 0 rgba(0, 0, 0, 0.05)";
    readonly sm: "0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px -1px rgba(0, 0, 0, 0.1)";
    readonly base: "0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -2px rgba(0, 0, 0, 0.1)";
    readonly md: "0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -4px rgba(0, 0, 0, 0.1)";
    readonly lg: "0 20px 25px -5px rgba(0, 0, 0, 0.1), 0 8px 10px -6px rgba(0, 0, 0, 0.1)";
    readonly xl: "0 25px 50px -12px rgba(0, 0, 0, 0.25)";
    readonly "2xl": "0 25px 50px -12px rgba(0, 0, 0, 0.25)";
    readonly inner: "inset 0 2px 4px 0 rgba(0, 0, 0, 0.05)";
    readonly none: "none";
};
declare const zIndex: {
    readonly hide: -1;
    readonly auto: "auto";
    readonly base: 0;
    readonly dropdown: 1000;
    readonly sticky: 1100;
    readonly fixed: 1200;
    readonly modalBackdrop: 1300;
    readonly modal: 1400;
    readonly popover: 1500;
    readonly tooltip: 1600;
    readonly notification: 1700;
    readonly max: 9999;
};
type SpacingToken = typeof spacing;
type BorderRadiusToken = typeof borderRadius;
type ShadowToken = typeof shadows;
type ZIndexToken = typeof zIndex;

export { type BorderRadiusToken, type ShadowToken, type SpacingToken, type ZIndexToken, borderRadius, shadows, spacing, zIndex };
