/**
 * VoiceAssist Utilities
 * Shared utility functions and helpers
 */
/**
 * Capitalizes the first letter of a string
 */
declare function capitalize(str: string): string;
/**
 * Truncates a string to a maximum length and adds ellipsis
 */
declare function truncate(str: string, maxLength: number): string;
/**
 * Converts a string to kebab-case
 */
declare function kebabCase(str: string): string;
/**
 * Converts a string to camelCase
 */
declare function camelCase(str: string): string;
/**
 * Formats a date to a human-readable string
 */
declare function formatDate(date: Date | string, format?: "short" | "long"): string;
/**
 * Returns a relative time string (e.g., "2 hours ago")
 */
declare function relativeTime(date: Date | string): string;
/**
 * Chunks an array into smaller arrays of specified size
 */
declare function chunk<T>(array: T[], size: number): T[][];
/**
 * Removes duplicate values from an array
 */
declare function unique<T>(array: T[]): T[];
/**
 * Shuffles an array randomly
 */
declare function shuffle<T>(array: T[]): T[];
/**
 * Deeply clones an object
 */
declare function deepClone<T>(obj: T): T;
/**
 * Picks specified keys from an object
 */
declare function pick<T extends object, K extends keyof T>(obj: T, keys: K[]): Pick<T, K>;
/**
 * Omits specified keys from an object
 */
declare function omit<T extends object, K extends keyof T>(obj: T, keys: K[]): Omit<T, K>;
/**
 * Validates an email address
 */
declare function isValidEmail(email: string): boolean;
/**
 * Validates a URL
 */
declare function isValidUrl(url: string): boolean;
/**
 * Checks if a value is empty (null, undefined, '', [], {})
 */
declare function isEmpty(value: unknown): boolean;
/**
 * Formats a file size in bytes to a human-readable string
 */
declare function formatBytes(bytes: number, decimals?: number): string;
/**
 * Formats a number with thousand separators
 */
declare function formatNumber(num: number): string;
/**
 * Debounces a function call
 */
declare function debounce<T extends (...args: unknown[]) => unknown>(func: T, wait: number): (...args: Parameters<T>) => void;
/**
 * Throttles a function call
 */
declare function throttle<T extends (...args: unknown[]) => unknown>(func: T, limit: number): (...args: Parameters<T>) => void;
/**
 * Checks if text contains potential PHI
 */
declare function containsPHI(text: string): boolean;
/**
 * Redacts potential PHI from text
 */
declare function redactPHI(text: string): string;

export { camelCase, capitalize, chunk, containsPHI, debounce, deepClone, formatBytes, formatDate, formatNumber, isEmpty, isValidEmail, isValidUrl, kebabCase, omit, pick, redactPHI, relativeTime, shuffle, throttle, truncate, unique };
